import { useState } from 'react';

const Example = () => {
  const personObj = { name: 'Tom', age: 18 };
  const [obj, setObj] = useState(personObj);

  const changeName = (e) => {
    setObj({ name: e.target.value, age: obj.age });
  };
  const changeAge = (e) => {
    setObj({ age: e.target.value });
  };

  return (
    <>
      <h1>Name:{obj.name}</h1>
      <h1>Age:{obj.age}</h1>

      <input type='text' onChange={changeName} value={obj.name}></input>
      <input type='number' onChange={changeAge} value={obj.age}></input>
    </>
  );
};

export default Example;
