import { useState } from 'react';

const Example = () => {
  const [num1, setNum1] = useState(0);

  <>
    <p>ボタンAを0回押しました</p>
    <button onClick={() => setNum1(num1 + 1)}>ボタンA</button>
  </>;
};

export default Example;
