import { useState } from 'react';

const Todo = () => {
  const [inputVal, setInputVal] = useState('');
  const todosList = [
    {
      id: 1,
      content: '店予約する',
    },
    {
      id: 2,
      content: '卵買う',
    },
    {
      id: 3,
      content: '郵便出す',
    },
  ];

  return (
    <>
      <h1>Reminder</h1>
      {todosList.map((todo) => {
        return (
          <div key={todo.id}>
            <button>完了</button>
            <label htmlFor={todo.content}>{todo.content}</label>
          </div>
        );
      })}

      <input
        type='text'
        value={inputVal}
        onChange={(e) => setInputVal(e.target.value)}
      />
    </>
  );
};

export default Todo;
