const Form = ({ inputVal, setInputVal, addTodo }) => {
  return (
    <>
      <input
        type='text'
        value={inputVal}
        onChange={(e) => setInputVal(e.target.value)}
      />
      <button onClick={addTodo}>追加</button>
    </>
  );
};
